package EncapsulationImpl;/* * @author:Saawan * @created:[10/24/2020],Saturday * Time:8:23 PM */public class Album {    public String name;    public String artist;    public double  price;    public int  numberOfCopies;    public void  sellCopies(){        if (numberOfCopies> 0){            numberOfCopies--;            System.out.printf("one album has sold");        }else {            System.out.print("No more album availabe");        }    }    public void orderCopies(int num){        numberOfCopies+=num;    }}