package EncapsulationImpl;/* * @author:Saawan * @created:[10/24/2020],Saturday * Time:8:23 PM */public class StockKeeper {    public String name;    public StockKeeper(String name){        this.name=name;    }    public void  manageAlbum(Album album, String name, String artist, double price,int numberOFCopies){        album.name=name;        album.artist=artist;        album.price=price;        album.numberOfCopies=numberOFCopies;        System.out.println("Album managed by:"+this.name);        System.out.println("Album details......");        System.out.println("Album name:"+album.name);        System.out.println("Album artist:"+album.artist);        System.out.println("Album price : "+album.price);        System.out.println("Album number of copies:" +album.numberOfCopies);    }    public static void main(String[] args) {        StockKeeper johndoe=new StockKeeper("John Doe");        johndoe.manageAlbum(new Album(),"Slippery when wet","Bon Jovi",-1000.00,-80);    }}