package EncapsulationImpl;/* * @author:Saawan * @created:[10/24/2020],Saturday * Time:8:56 PM */public class StockKeeper2 {    private String name;    StockKeeper2(String name){        setName(name);    }    public void manageAlbum(Album2 album,String name,String artist,double price,int numberOfCopies){        album.setName(name);        album.setArtist(artist);        album.setPrice(price);        album.setnumberOfCopies(numberOfCopies);        System.out.println("Album managed by :"+getName());        System.out.println("Album details :::::::");        System.out.println("Album name:"+album.getName());        System.out.println("Album artist: "+album.getArtist());        System.out.println("Album price: "+album.getPrice());        System.out.println("Album numbert of copies :"+album.getNumberOfCopies());    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public static void main(String[] args) {        StockKeeper2 john=new StockKeeper2("John");        john.manageAlbum(new Album2(),                "Slippery when wet",                "Bon Jovi",                -1000,                -50);    }}