package StackImplement;import java.util.Stack;/* * @author:Saawan * @created:[10/17/2020],Saturday * Time:9:43 PM * --> */public class ParenthesisCheck {    static class Parenthesis{        private char parenthesis;        private  int position;        public Parenthesis(char par,int pos){            this.parenthesis=par;            this.position=pos;        }        @Override        public String toString() {            return this.parenthesis+"From postition: "+this.position;        }    }    public static void main(String[] args) {        String data="((()))()(";        char[] chars=data.toCharArray();        Stack<Parenthesis>stack=new Stack<Parenthesis>();        for (int i = 0; i < chars.length; i++) {            if (chars[i]=='('){                stack.push(new Parenthesis('(',i+1));            }else  if (chars[i]==')'){                if (!stack.empty()){                    stack.pop();                }else {                    System.out.println(") from position: "+(i+1));                    return;                }            }        }        if (!stack.empty()){            for (int i = 0; i < stack.size(); i++) {                System.out.println(stack.pop());            }        }else {            System.out.println("No errors");        }    }}